package com.citelic.game.entity.npc.impl.glacor.attacks;import com.citelic.game.entity.Animation;import com.citelic.game.entity.Entity;import com.citelic.game.entity.Hit;import com.citelic.game.entity.Hit.HitLook;import com.citelic.game.entity.npc.impl.glacor.Glacor;import com.citelic.game.entity.npc.impl.glacor.GlacorAttacks;import com.citelic.utility.Utilities;/** *  *  *  * @author Tyler Represents The glacors melee attack. */public class MeleeAttack implements GlacorAttacks {	@Override	public int attack(Glacor glacor, Entity target) {		glacor.setNextAnimation(new Animation(9955));		target.applyHit(new Hit(target, Utilities.random(312),				HitLook.MELEE_DAMAGE));		return 5;	}}